import React, { useState } from 'react';
import PropTypes from 'prop-types';

const LB_TO_KG = 0.45359237;

const SBD = ({ name, currentWeight, goalWeight, unit }) => (
  <div className='container'>
    <div className='info'>{name}:</div>
    <div className='number'><b>{currentWeight}</b> lbs - Goal: <b>{goalWeight}</b> lbs</div>
  </div>
);

const SBDSta = ({ sbd }) => {
  const [unit, setUnit] = useState('LBS');

  const toggleWeightUnit = () => {
    setUnit(prevUnit => prevUnit === 'LBS' ? 'kg' : 'LBS');
    const newSbd = sbd.map(item => ({
      ...item,
      currentWeight: convertWeight(item.currentWeight, unit),
      goalWeight: convertWeight(item.goalWeight, unit)
    }));
    setSbd(newSbd);
  };

  const convertWeight = (weight, currentUnit) => {
    return currentUnit === 'LBS' ? (weight * LB_TO_KG).toFixed(2) : (weight / LB_TO_KG).toFixed(2);
  };

  // This will convert the weights according to the current unit for display purposes.
  const sbdWithConvertedWeights = sbd.map(item => ({
    ...item,
    currentWeight: unit === 'kg' ? convertWeight(item.currentWeight, 'LBS') : item.currentWeight,
    goalWeight: unit === 'kg' ? convertWeight(item.goalWeight, 'LBS') : item.goalWeight,
    unit: unit
  }));

  return (
    <div className="sbdStatists">
      <div className="title">
        <h3>Strength Benchmarks</h3>
      </div>
      <button onClick={toggleWeightUnit}>Switch to {unit === 'LBS' ? 'kg' : 'LBS'}</button>
      <article className="sbd-container">
        {sbdWithConvertedWeights.map((item) => (
          <SBD
            key={item.name}
            name={item.name}
            currentWeight={item.currentWeight}
            goalWeight={item.goalWeight}
            unit={item.unit}
          />
        ))}
      </article>
    </div>
  );
};

SBDSta.propTypes = {
  sbd: PropTypes.arrayOf(PropTypes.shape({
    name: PropTypes.string.isRequired,
    currentWeight: PropTypes.string.isRequired,
    goalWeight: PropTypes.string.isRequired,
    unit: PropTypes.string.isRequired,
  })).isRequired,
};

SBD.propTypes = {
  name: PropTypes.string.isRequired,
  currentWeight: PropTypes.string.isRequired,
  goalWeight: PropTypes.string.isRequired,
  unit: PropTypes.string.isRequired,
};

export default SBDSta;
