import React from 'react';
import PropTypes from 'prop-types';
import Markdown from 'markdown-to-jsx';

const Job = ({ data }) => (
    //   name: 
    //   position: 
    //   companyProfile:
    //   startDate: 
    //   endDate: 
    //   summary: 
    //   techStack
    <article className="jobs-container">
      <header>
        <div>
            <h4 className='company'>{data.name}</h4>
            <p className='location'>{data.location}</p>
        </div>
        <div>
            <p className="position">{data.position}</p>
            <p className='year'>{dayjs(data.startDate).format('MMMM YYYY')} - {endDate ? dayjs(data.endDate).format('MMMM YYYY') : 'PRESENT'}</p>
        </div>
        <Markdown
            options={{
                overrides: {
                    p: {
                        props: {
                            className: 'companyProfile',
                        },
                    },
                },
            }}
        >
        {companyProfile}
        </Markdown>
        <ul className="points">
            {highlights.map((summary) => (
            <li key={summary}>{summary}</li>
        ))}
      </ul>
      </header>
    </article>
);

const Experience = ({ data }) => (
    <div className="experience">
        <div className="link-to" id="experience" />
        <div className="title">
        <h3>Experience</h3>
        </div>
        {data.map((job) => (
        <Job
            data={job}
            key={`${job.name}-${job.position}`}
        />
        ))}
    </div>
);

Experience.propTypes = {
    data: PropTypes.arrayOf(PropTypes.shape({
        name: PropTypes.string,
        position: PropTypes.string,
        companyProfile: PropTypes.string,
        startDate: PropTypes.string,
        endDate: PropTypes.string,
        summary: PropTypes.string,
    })),
};

Job.propTypes = {
    data: PropTypes.shape({
        name: PropTypes.string.isRequired,
        position: PropTypes.string.isRequired,
        companyProfile: PropTypes.string.isRequired,
        startDate: PropTypes.string.isRequired,
        endDate: PropTypes.string,
        summary: PropTypes.string,
    }).isRequired,
};

export default Experience;
