import React, { useState } from 'react';
import PropTypes from 'prop-types';

const LB_TO_KG = 0.45359237;

const SBD = ({ name, currentWeight, goalWeight, unit }) => (
  <div className='container'>
    <div className='info'>{name}:</div>
    <div className='number'><b>{currentWeight}</b> {unit} - Goal: <b>{goalWeight}</b> {unit}</div>
  </div>
);

const SBDSta = ({ initialSbd }) => {
  const [sbd, setSbd] = useState(initialSbd); // Define state for the SBD data
  const [unit, setUnit] = useState('LBS'); // Initial unit is LBS

  const toggleWeightUnit = () => {
    const newUnit = unit === 'LBS' ? 'kg' : 'LBS';
    const convertedSbd = sbd.map(item => ({
      ...item,
      currentWeight: convertWeight(item.currentWeight, newUnit),
      goalWeight: convertWeight(item.goalWeight, newUnit),
      unit: newUnit
    }));
    setSbd(convertedSbd); // Update the state with the new converted weights
    setUnit(newUnit); // Update the state of the unit
  };

  const convertWeight = (weight, targetUnit) => {
    return targetUnit === 'kg' ? (weight * LB_TO_KG).toFixed(2) : (weight / LB_TO_KG).toFixed(2);
  };

  return (
    <div className="sbdStatists">
      <div className="title">
        <h3>Strength Benchmarks</h3>
      </div>
      <button onClick={toggleWeightUnit}>Switch to {unit === 'LBS' ? 'kg' : 'LBS'}</button>
      <article className="sbd-container">
        {sbd.map((item) => (
          <SBD
            key={item.name}
            name={item.name}
            currentWeight={item.currentWeight}
            goalWeight={item.goalWeight}
            unit={item.unit}
          />
        ))}
      </article>
    </div>
  );
};

SBDSta.propTypes = {
  initialSbd: PropTypes.arrayOf(PropTypes.shape({
    name: PropTypes.string.isRequired,
    currentWeight: PropTypes.oneOfType([
      PropTypes.string,
      PropTypes.number,
    ]).isRequired,
    goalWeight: PropTypes.oneOfType([
      PropTypes.string,
      PropTypes.number,
    ]).isRequired,
    unit: PropTypes.string.isRequired,
  })).isRequired,
};

SBD.propTypes = {
  name: PropTypes.string.isRequired,
  currentWeight: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.number,
  ]).isRequired,
  goalWeight: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.number,
  ]).isRequired,
  unit: PropTypes.string.isRequired,
};

export default SBDSta;
