import React, { useState } from 'react';
import PropTypes from 'prop-types';

const WeightConverter = ({ initialWeight, initialUnit, data }) => {
    const [weight, setWeight] = useState(initialWeight);
    const [unit, setUnit] = useState(initialUnit);

    const convertWeight = (currentUnit, weight) => {
      return currentUnit === 'kg' ? data.weightUnitLB : data.weightUnitKG;
    };

    const toggleUnit = () => {
      const newUnit = unit === 'kg' ? 'LBS' : 'kg';
      setWeight((prevWeight) => convertWeight(unit, prevWeight).toFixed(2));
      setUnit(newUnit);
    };
};


const BasicSta = ({ data }) => (
    <div className="basicStatists">
        <div className="link-to" id="stat" />
        <div className="title">
            <h3>Basic Statistics</h3>
        </div>
        <article className="basic-container">
            <div className='container'>
                <div className='info'>{data.age}:</div>
                <div className='number'><b>{data.ageNum}</b>  {data.ageUnit}</div>
            </div>
            <div className='container'>
                <div className='info'>{data.weight}:</div>
                <div className='number'><b>{weight}</b>  {unit}</div>
                <button onClick={<WeightConverter data={data} initialWeight={data.weightUnitLB} initialUnit={data.unit}/>}>change</button>
            </div>
            <div className='container'>
                <div className='info'>{data.height}:</div>
                <div className='number'><b>{data.heightNumFT}</b>  {data.heighttUnitFT}</div>

            </div>
        </article>
    </div>
);

BasicSta.propTypes = {
    data: PropTypes.arrayOf(PropTypes.shape({
      info: PropTypes.string,
      stat: PropTypes.number,
      measurement: PropTypes.string,
    })),
};

Basic.propTypes = {
    data: PropTypes.shape({
      info: PropTypes.string.isRequired,
      stat: PropTypes.number.isRequired,
      measurement: PropTypes.string.isRequired,
    }).isRequired,
};

export default BasicSta;
