import React from 'react';
import PropTypes from 'prop-types';
import Markdown from 'markdown-to-jsx';
import dayjs from 'dayjs';

const Job = (
    {
        data: {
          name, position, location, companyProfile, startDate, endDate, summary,
        },
      }) => (
    //   name: 
    //   position: 
    //   companyProfile:
    //   startDate: 
    //   endDate: 
    //   summary: 
    //   techStack
    <article className="jobs-container">
      <header>
        <div>
            <h4 className='company'>{name}</h4>
            <p className='location'>{location}</p>
        </div>
        <div>
            <p className="position">{position}</p>
            <p className='year'>{dayjs(startDate).format('MMMM YYYY')} - {endDate ? dayjs(endDate).format('MMMM YYYY') : 'PRESENT'}</p>
        </div>
        <Markdown
            options={{
                overrides: {
                    p: {
                        props: {
                            className: 'companyProfile',
                        },
                    },
                },
            }}
        >
        {companyProfile}
        </Markdown>
        <ul className="points">
           {summary.map((point) => (
                <li key={point}>{point}</li>
                ))
            }
      </ul>
      </header>
    </article>
);

const Experience = ({ data }) => (
    <div className="experience">
        <div className="link-to" id="experience" />
        <div className="title">
        <h3>Experience</h3>
        </div>
        {data.map((job) => (
        <Job
            data={job}
            key={`${job.name}-${job.position}`}
        />
        ))}
    </div>
);

Experience.propTypes = {
    data: PropTypes.arrayOf(PropTypes.shape({
        name: PropTypes.string,
        position: PropTypes.string,
        companyProfile: PropTypes.string,
        startDate: PropTypes.string,
        endDate: PropTypes.string,
        summary: PropTypes.arrayOf(PropTypes.string),
    })),
};

Job.propTypes = {
    data: PropTypes.shape({
        name: PropTypes.string.isRequired,
        position: PropTypes.string.isRequired,
        companyProfile: PropTypes.string.isRequired,
        startDate: PropTypes.string.isRequired,
        endDate: PropTypes.string.isRequired,
        summary: PropTypes.arrayOf(PropTypes.string.isRequired),
    }).isRequired,
};

export default Experience;
