const extracurricular = [
    {
        name: 'Guangyuan Program',
        startDate: '2020-06-01',
        endDate: '2022-12-01',
        summary: [
            'Spearheaded a community-driven effort during the pandemic to collect and refurbish unused internet devices, enabling children in technologically underserved areas to continue their education remotely.',
            'Conducted in-depth research on the digital divide, crafted comprehensive reports, and engaged in data collection efforts within third-world regions to assess needs and impact.'
        ],
    },
    {
        name: 'Research On the Development of Sex Education',
        startDate: '2019-01-01',
        endDate: '2020-06-01',
        summary: [
            'Forged meaningful partnerships with eight diverse sex education organizations, shedding light on the critical need for tailored educational content across various age groups.',
            'Produced an insightful market research report that digs into the heart of our target demographics and maps out the landscape of sex education development in Southeast China.',
            'Pioneered an innovative sex education model that intertwines storytelling with learning through comics, successfully integrating this resource into schools and fostering a culture of respect for girls from a young boys’ perspective.'
        ],
    },
    {
        name: 'Shared Bicycle Recycling Project',
        startDate: '2018-12-01',
        endDate: '2020-03-01',
        summary: [
            "Spearheaded a comprehensive market analysis initiative focusing on Guangzhou's bicycle-sharing ecosystem, overseeing the strategic allocation and management of research personnel.",
            "Championed the data acquisition efforts, orchestrating a robust team to gather essential project information through strategic telephone outreach and meticulous field visit methodologies. Successfully deployed and analyzed data from approximately 1,000 distributed questionnaires, culminating in a nuanced visual analysis of user behavior patterns.",
            "Developed forward-thinking recycling strategies for decommissioned shared bicycles, constructing a detailed cost allocation framework to accurately project the financial implications of each proposed recycling solution."
        ],
    },
]

export default extracurricular;
